    int threeSumMulti(vector<int> &&arr, int target)
    {
        int nums[101] = {0};
        for (int i : arr)
        {
            ++nums[i];
        }
        long res = 0, mod = 1e9 + 7;
        for (int i = 0; i < 101; ++i)
        {
            if (nums[i] == 0)
                continue;
            for (int j = 100; j >= i; --j)
            {
                int k = target - i - j;
                if (k >= i && k <= j)
                {
                    if (i == k && j == k)
                    {
                        if (nums[k] >= 3)
                            res += ((nums[i] * (nums[j] - 1)) % mod) * (nums[k] - 2) / 6;
                    }
                    else if (i < k && j > k)
                    {
                        res += nums[i] * nums[j] * nums[k];
                    }
                    else if (nums[k] >= 2)
                    {
                        res += ((nums[i] * nums[j]) % mod) * (nums[k] - 1) / 2;
                    }
                    res %= mod;
                }
                else if (k > j)
                {
                    break;
                }
            }
        }
        return res;
    }
